  return '';
})() }}",
  "subject": "{{ (() => {
  const parts = (($json.name || '').replace(/\.pdf$/i,'')).split('_');
  const kept = parts.filter(p =>
    !/^(CBSE|ICSE)$/i.test(p) &&
    !/^Class\d{1,2}$/i.test(p) &&
    !/^20\d{2}(?:[-_]\d{2})?$/.test(p) &&
    !/^(Syllabus|MarkingScheme|Blueprint|Practical)$/i.test(p)
  );
  // remove leftover trailing 2-digit year piece like "24"
  if (kept.length && /^\d{2}$/.test(kept[kept.length - 1])) kept.pop();
  return kept.join(' ');
})() }}",
  "year": "{{ (() => {
  const s = $json.name || '';
  const m = s.match(/20\d{2}[-_]\d{2}/) || s.match(/20\d{2}/);
  return m ? m[0].replace(/[_]/g,'-') : '';
})() }}",
  "doc_type": "{{ ((($json.name||'').match(/(Syllabus|MarkingScheme|Blueprint|Practical)/) || [,'Syllabus'])[1]) }}",
  "doc_group": "{{ (() => { 
  const n = ($json.name||'').replace(/\.pdf$/i,'');
  const board = n.split('_')[0] || '';
  const m = n.match(/Class(\d{1,2})/g) || [];
  const grade = m.map(s => s.replace('Class','')).join('-');
  const parts = n.split('_');
  const lastClassIdx = [...parts.keys()].filter(i => /^Class\d{1,2}$/i.test(parts[i])).pop() ?? -1;
  const stopIdx = parts.findIndex((p,i)=> i>lastClassIdx && /^(Syllabus|MarkingScheme|Blueprint|Practical)$/i.test(p));
  const end = stopIdx > -1 ? stopIdx : parts.length;
  const subject = parts.slice(lastClassIdx + 1, end).join(' ').replace(/_+/g,' ').trim();
  return [board, grade, subject].filter(p => p && p.trim()).join(' ');
})() }}"
}

parameters.jsCode:
// Input from Telegram: $json.message.text
// Output: { query, board, grade, subject, year, doc_type }

const textRaw = ($input.first().json.message.text || '').trim();
const t = textRaw.toLowerCase();

// 1) board
let board = null;
if (/\bicse\b/.test(t)) board = 'ICSE';
if (/\bcbse\b/.test(t)) board = 'CBSE';

// 2) year: 2023-24 / 2023_24 / 2026
let year = null;
const my = t.match(/20\d{2}[-_]\d{2}/) || t.match(/20\d{2}/);
if (my) year = my[0].replace(/_/g,'-');

// 3) doc_type
let doc_type = null;
if (/mark(ing)?[-\s]?scheme/.test(t)) doc_type = 'MarkingScheme';
else if (/blue\s?print/.test(t))      doc_type = 'Blueprint';
else if (/practical/.test(t))         doc_type = 'Practical';
else if (/syllabus|syllabi/.test(t))  doc_type = 'Syllabus';

// 4) grade (supports "class 12", "12" with 'class', or "9-10")
let grade = null;
const gRange = t.match(/\b(\d{1,2})\s*[-–]\s*(\d{1,2})\b/);
const gClass = t.match(/\bclass\s*(\d{1,2})\b/);
if (gRange) { grade = gRange[1] + '-' + gRange[2];
            } else if (gClass) { grade = gClass[1];
                               }                 
// roman numerals / words
const mRoman=t.match(/\bclass\s*(ix|x|xi|xii)\b/i);
if (mRoman){
  const v = mRoman[1].toLowerCase();
  if (v === 'ix') grade = '9';
  else if (v === 'x') grade = '10';
  else if (v === 'xi') grade = '11';
  else if (v === 'xii') grade = '12';
}

const mWord=t.match(/\bclass\s*(nine|ten|eleven|twelve)\b/i);
if (mWord){
  const w = mWord[1].toLowerCase();
  if (w === 'nine') grade = '9';
  else if (w === 'ten') grade = '10';
  else if (w === 'eleven') grade = '11';
  else if (w === 'twelve') grade = '12';
}

// 5) subject (dictionary + fallback)
const dict = [
  'biology','chemistry','physics','mathematics','maths','computer applications',
  'computer science','english core','english elective','english','economics',
  'political science','general studies','business studies','yoga','sociology',
  'history','geography','hindi','kannada','tamil','telugu','malayalam','marathi',
  'legal studies','home science','environmental science','environmental applications',
  'mass media communication','hospitality management','technical drawing applications',
  'commercial studies','classical language','psychology','physical education',
  'ncc','social science'
];

let subject = null;
let best = '';
for (const s of dict) {
  const patt = '\\b' + s.replace(/\s+/g,'\\s+')+'\\b';
  const re = new RegExp(patt,'i');
  if (re.test(textRaw) && s.length > best.length) best = s;
}
if (best) {
  // normalize title case & a few names
  const norm = best
    .replace(/\bmaths\b/i,'mathematics')
    .replace(/\benglish core\b/i,'English Core')
    .replace(/\benglish elective\b/i,'English Elective')
    .replace(/\bcomputer applications\b/i,'Computer Applications')
    .replace(/\bcomputer science\b/i,'Computer Science')
    .replace(/\bpolitical science\b/i,'Political Science');
  subject = norm.split(' ')
    .map(w => w.charAt(0).toUpperCase()+w.slice(1))
    .join(' ');
}

// fallback: strip known tokens and keep remainder
if (!subject) {
  let tmp = t
    .replace(/\b(cbse|icse)\b/gi,' ')
    .replace(/class\s*(ix|x|xi|xii|\d{1,2})(\s*–\s*(ix|x|xi|xii|\d{1,2}))?/gi,' ')
    .replace(/20\d{2}([_-]\d{2})?/g,' ')
    .replace(/syllabus|mark(ing)?[-\s]?scheme|blue\s?print|practical/gi,' ')
    .replace(/\b(for|of|in|on|the)\b/gi,' ')
    .replace(/[^\p{L}\p{N}\s]/gu,' ')
    .replace(/\s+/g,' ')
    .trim();
  if (tmp) {
    subject = tmp.split(' ')
      .map(w => w.charAt(0).toUpperCase()+w.slice(1))
      .join(' ');
  }
}

return [{ json: { query: textRaw, board, grade, subject, year, doc_type } }];

// Guardrail for Syllabus Bot
// Input (from previous node): { query, board, grade, subject, year, doc_type }
// Output: same fields + guard_ok (boolean) + guard_reason (string)

const j = $json;

// normalize helper
const norm = v => (v ?? '').toString().trim();

// normalized fields
const board = norm(j.board).toUpperCase();
const grade = norm(j.grade);
const subject = norm(j.subject);
const docType = norm(j.doc_type);

// rules
const validBoard = ['CBSE', 'ICSE'].includes(board);
const hasDetail = [grade, subject, docType].some(v => v.length > 0);

// result
const guard_ok = validBoard && hasDetail;

let guard_reason = 'ok';
if (!validBoard) {
  guard_reason = 'Please specify the board as CBSE or ICSE.';
} else if (!hasDetail) {
  guard_reason = 'Please include at least one of: grade (e.g., "12" or "9-10"), subject (e.g., "Physics"), or document type ("Syllabus", "MarkingScheme", "Blueprint", "Practical").';
}

// return merged item
return {
  json: {
    ...j,
    // keep cleaned values if you want to pass them on
    board: board || j.board,
    grade,
    subject,
    doc_type: docType,
    guard_ok,
    guard_reason,
  }
};
